import './App.css';
import Login from './components/sessions/Login'
import Navigation from './components/Navigation';
import UserHomepage from './components/user_components/UserHomepage'
import UserFavorites from './components/coffee_shops/UserFavorites';
import {useEffect, useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  BrowserRouter,
  Routes,
  Route
} from "react-router-dom"
import { getUser } from './custom_modules/getUser';




function App() {

  const [loggedIn, setLoggedIn ] = useState(true)
  const [user, setUser] = useState(null)

  useEffect((
    () => {
      getUser(setUser)
    }
  ),[])


  return (
    <BrowserRouter>
      <Routes>
        <Route path="home" element={
          <div className="App">
            <Navigation user={user} isLoggedIn={setLoggedIn} />
            {loggedIn ? 
              <UserHomepage user={user} setUser={setUser} setLoggedIn={setLoggedIn} /> 
                : 
              <Login  isLoggedIn={setLoggedIn} />
             }
          </div>
        } />
        <Route path="favorites" element={<UserFavorites user={user} />} />
        <Route path="profile" element={"<Profile/>"} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
