const axios = require('axios')


const findFavorite = (userId, locationId) => {
    axios.get(`/favorites/?user_id=${userId}&location_id=${locationId}`).then(
        (res) => {
            console.log(res.data)
        }
    )

}

const createFavorite = (userId, locationId) => {
    axios.post(`/favorites`, {
        "user_id": userId,
        "location_id": locationId
    })

}

const deleteFavorite = (userId, locationId) => {
    axios.delete(`/favorites/?user_id=${userId}&location_id=${locationId}`)

}

module.export = {createFavorite, findFavorite, deleteFavorite}
