import React, { useState } from "react";
import LoginForm from './LoginForm'
import LoginButton from './LoginButton'
import Signup from "./Signup";

const Login = ({isLoggedIn}) => {
    const [isClicked, setIsClicked ] = useState(true)
    const [renderModal, setRenderLoginForm] = useState(true)

    const displayLoginForm = arg => {
        setIsClicked(arg)
        isLoggedIn(true)
    }

    return (
        <>
            { renderModal ? <LoginForm isLoggedIn={isLoggedIn} renderModal={setRenderLoginForm}  /> : <LoginButton displayLoginForm={displayLoginForm} is isClicked={isClicked} />}
            <Signup />
        </>
    )

}

export default Login
