import React, { useState } from "react"
import SignupButton from "./SignupButton"
import SignupForm from "./SignupForm"

const Signup = () => {
    const [ isClicked, setIsClicked ] = useState(false)
    // const isClicked

    const displaySignupForm = (arg) => {
        setIsClicked(arg)
    }

    return (
        <>

            <SignupButton displaySignupForm={displaySignupForm} isClicked={isClicked}/>
            {isClicked ? <SignupForm displaySignupForm={displaySignupForm} isClicked={isClicked} /> : null}
        </>
    )
}

export default Signup
