import React, { useState } from "react";
import LoginForm from './LoginForm'
import LoginButton from './LoginButton'
import Signup from "./Signup";

const Login = ({isLoggedIn}) => {
    const [renderLoginForm, setRenderLoginForm] = useState(true)
    const [renderSignupForm, setRenderSignupForm] = useState(false)

    return (
        <>
            { renderLoginForm ? <LoginForm isLoggedIn={isLoggedIn} renderModal={setRenderLoginForm}  /> : <LoginButton renderModal={setRenderLoginForm} />}


        </>
    )

}

export default Login
