import React, {useEffect, useState} from "react";
import axios from "axios";
import Navigation from "../Navigation";
import LogoutButton from "../sessions/LogoutButton";
import UserWelcome from "./UserWelcome";
import RenderCoffeeShop from "../coffee_shops/RenderCoffeeShop";
import { Container } from "react-bootstrap";
import userLocation from "../../custom_modules/userLocation"


const UserHomepage = ({isLoggedIn}) => {
    const [user, setUser] = useState(null)
    const [location, setLocation] = useState(null)
    const [error, setError] = useState(null)

    useEffect(() => {
        axios
            .get('/me')
            .then((res) => {
                setUser(res.data) 
                // userLocation.getUserLocation().then(res => {
                //     setLocation(res)
                // })
                console.log(navigator.geolocation.getCurrentPosition())
                isLoggedIn(true)
            })
            .catch(error => {
                setError(error)
                isLoggedIn(false)
            })
    }, [isLoggedIn])

    // if (error) return `${error}`

    return(
        <>
            <Navigation />
            <Container>
                {user ? <UserWelcome userName={user.first_name} /> : null}
                {console.log(location)}
                {location ? <RenderCoffeeShop location={location} /> : null}
                <LogoutButton isLoggedIn={isLoggedIn} />
            </Container>
        </>
    )
}

export default UserHomepage
