const axios = require('axios')


export const findFavorite = (userId, locationId, setFavorite) => {
    return axios.get(`/favorites/?user_id=${userId}&location_id=${locationId}`).then(
        (res) => {
            setFavorite(res.data)
        }
    )

}

export const createFavorite = (userId, locationId) => {
    return axios.post(`/favorites`, {
        "user_id": userId,
        "location_id": locationId
    })

}

export const deleteFavorite = (userId, locationId) => {
    return axios.delete(`/favorites/?user_id=${userId}&location_id=${locationId}`)

}

// module.export = {createFavorite, findFavorite, deleteFavorite}
