const axios = require('axios')


const getUserLocation = async () => {
    const userLocation = {}

    const displayLocationInfo = async position => {
        const lng  = await position.coords.longitude;
        const lat = await position.coords.latitude;

        userLocation = {
            lng,
            lat
        }

        return userLocation
        // return (
        //     {
        //         lng,
        //         lat
        //     }
        // )
    }


    if (navigator.geolocation) {
        // console.log(navigator.geolocation.getCurrentPosition(displayLocationInfo))
        navigator.geolocation.getCurrentPosition(displayLocationInfo);    
    }



    // return await axios.get("https://geolocation-db.com/json/8dd79c70-0801-11ec-a29f-e381a788c2c0")
    //         .then((res) => {
    //             // console.log(navigator.geolocation.getCurrentPosition())
    //             return res.data
    //         })

}

module.exports = { getUserLocation }
