import axios from "axios";
import React, { useEffect, useState } from "react";
import CoffeeShopCard from "./CoffeeShopCard";
import  Row from "react-bootstrap/Row";
import  Col from "react-bootstrap/Col";



const RenderKafficoCoffeeShops = ( props ) => {
    const [ coffeeShops, setCoffeeShops ] = useState([])

    useEffect(() => {
        const location = props.location
        axios.get(`/locations`)
        .then((res) => {
            setCoffeeShops(res.data[0])
        })
    }, [props.location])

    const renderCoffeeShops = () => {
        return coffeeShops.map((coffeeShop, idx) => {
            return( 
                <Col key={idx} >
                    <CoffeeShopCard currentUserLocation={props.location} coffeeShop={coffeeShop} />
                </Col>
            )
        })

    };

    return (
        <Row>
            <p>Test</p>
            {renderCoffeeShops()}
        </Row>
    )

}

export default RenderKafficoCoffeeShops
